import"https://cdn.jsdelivr.net/npm/swiper@10/swiper-bundle.min.js";const i=document.querySelectorAll(".navbar-nav .nav-link");i.forEach(function(t){t.addEventListener("click",()=>{i.forEach(function(e){e.classList.remove("active")}),t.classList.add("active")})});window.addEventListener("scroll",function(){const t=document.querySelector(".navbar"),e=document.querySelector(".navbar-brand img"),n=window.scrollY||document.documentElement.scrollTop,o=150,c=o/2;window.innerWidth>=768&&(n>0?(t.style.height=c+"px",e.style.width=c/o*100+"%"):(t.style.height=o+"px",e.style.width="100%"))});function r(){const t=window.pageYOffset||document.documentElement.scrollTop;i.forEach(e=>{const n=document.querySelector(e.getAttribute("href"));if(n){const o=n.offsetTop,c=n.offsetHeight;t>=o-50&&t<o+c-50&&(i.forEach(s=>{s.classList.remove("active")}),e.classList.add("active"))}})}window.addEventListener("scroll",r);
